import React,{useEffect,useState} from "react";
import jwt from 'jsonwebtoken';
import { useHistory } from "react-router-dom";
import Header from "./Header";

const Vendor=()=>{
    const history = useHistory();

    const[name,setName]=useState('');
    const[phone,setPhone]=useState('');
    const[fooditem,setFooditem]=useState('');
    const[amount,setAmount]=useState('');
    const[date,setDate]=useState('');

    const logout=()=>{
        window.localStorage.clear();
        window.location.href='/login'
    }

    async function addVendor(event){
        event.preventDefault();

        const response = await fetch('https://localhost:1337/api/vendor',{
            method: 'POST',
            headers: {
                'Content-type':'application/json'
            },
            body: JSON.stringify({
                name,
                phone,
                fooditem,
                amount,
                date
            }),
        })
        const data=await response.json();
        if(data.status=='ok'){
            alert('Vendor Added')
            history.push('/vendor');
        } 
    }




    return(
        <div>
            <h1>Add Vendor Data</h1>
            <form onSubmit={addVendor}>
                <label>Name:</label>
                <input value={name} onChange={(e)=>setName(e.target.value)}></input>
                <label>Phone:</label>
                <input value={phone} onChange={(e)=>setPhone(e.target.value)} type="number"></input>
                <label>FoodItem:</label>
                <input value={fooditem} onChange={(e)=>setFooditem(e.target.value)} type="text"></input>
                <label>Amount:</label>
                <input value={amount} onChage={(e)=>setAmount(e.target.value)} type="number"></input>
                <labe>Date:</labe>
                <input value={date} onChange={(e)=>setDate(e.target.value)} type="date"></input>
                <button>Add</button>
            </form>

            <button onClick={logout}>Logout</button>
        </div>
    )
} 
export default Vendor;
