import React,{useEffect, useState} from "react";
import { useHistory } from "react-router-dom";
import jwt from "jsonwebtoken";
import GooglePayButton from '@google-pay/button-react';


function loadScript(src) {
    return new Promise((resolve) => {
      const script = document.createElement("script");
      script.src = src;
      script.onload = () => {
        resolve(true);
      };
      script.onerror = () => {
        resolve(false);
      };
      document.body.appendChild(script);
    });
  }



const MyBill=()=>{
    const history=useHistory();
    const [messbill, setMessbill]=useState('');
    const [hostelbill, setHostelbill]=useState('');
    const [roll, setRoll]=useState('');

    useEffect(()=>{
        const token=localStorage.getItem('token');
        if(token){
            const user = jwt.decode(token)
            if(!user){
                localStorage.removeItem('token');
                history.replace('/login')
            }
            getBill();
        }
        else{
            history.push('/login')
        }
    })

    const goBack=()=>{
        window.location.href="/studentdashboard"
    }

    const payBill=()=>{

    }

    const getBill=()=>{
        const token = localStorage.getItem('token');
        const user = jwt.decode(token)
        fetch('http://localhost:1337/api/mybill',)
        .then((response)=>response.json())
        .then((json)=>{
            for(let i=0;i<json.user.length;i=i+1){
                if(user.roll==json.user[i].roll){
                    console.log(user.roll);
                    setRoll(user.roll);
                    setHostelbill(json.user[i].hostelbill);
                    setMessbill(json.user[i].messbill);
                }
            }

        })
    }

    async function addHostel(event){
        event.preventDefault();

        const response = await fetch('http://localhost:1337/api/hostelbill',{
            method: 'POST',
            headers:{
                'Content-Type':'application/json',
            },
            body: JSON.stringify({
                hostelbill,
                roll
            })
        })

        const data = await response.json()

        if(data.status=='ok'){
            alert("Bill Added");
            window.location.href="hostelbill"
        }
    }


    //Changes Made From Here

    async function showRazorpay() {
        const res = await loadScript(
          "https://checkout.razorpay.com/v1/checkout.js"
        );

        if (!res) {
          alert("Razorpay SDK failed to load. Are you online?");
          return;
        }

        const data = await fetch("http://localhost:1337/api/razorpay", {
            method: "POST",
            headers:{
                'Content-Type':'application/json',
            },
                body: JSON.stringify({
                    amount:hostelbill,
            })
        }).then((t) => t.json());

        console.log(data);

        const options = {
          key: "rzp_test_0tpemkHKm5K1Bc",
          currency: data.currency,
          amount: hostelbill*100,
          order_id: data.id,
          name: "Hostel Fees",
          description: "Amount To Be Paid For Hostel Room Service",
          handler: function (response) {
            // alert(response.razorpay_payment_id);
            // alert(response.razorpay_order_id);
            // alert(response.razorpay_signature);

            addHostel();
            async function addHostel(event){


                const response = await fetch('http://localhost:1337/api/hostelbill',{
                    method: 'POST',
                    headers:{
                        'Content-Type':'application/json',
                    },
                    body: JSON.stringify({
                        hostelbill,
                        roll
                    })
                })

                const data = await response.json()

                if(data.status=='ok'){
                    alert("Bill Added");
                    window.location.href="hostelbill"
                }

            }

          },
          prefill: {
            name: "Your Name",
            email: "email@example.com",
            phone_number: "1234567890",
          },
        };
        const paymentObject = new window.Razorpay(options);
        paymentObject.open();
      }



    return(
        <div>
            <h1>My Bill</h1>
            <p>My Roll: {roll}</p>
            <p>Mess Bill: {messbill}</p>
            <p>Hostel Bill: {hostelbill}</p>
            <button onClick={goBack}>Go Back</button>
            <button onClick={payBill}>Pay Bill</button>

            <div></div>

            <p>Razorpay payment portal ezzzz</p>
        <button onClick={showRazorpay}>Room Fee</button>

        </div>
    )
} 
export default MyBill;
