import React from "react";
import { useHistory } from "react-router-dom";
import Header from './Header'

const item=[];

export default class Fetch extends React.Component{

    state={
        loading: true,
        person: null,
    }

    async componentDidMount(){
        const url="http://localhost:1337/api/clean";
        const response = await fetch(url);
        const data = await response.json();
        for(let i=0;i<data.user.length;i=i+1){
            this.setState({person: data, loading: false})
            item.push(data.user[i]);
            console.log(data.user[0]);
        }
    }

    async handleClick(room){
        try{
            const response=await post('/deleteclean',{room});
            console.log(response);
        } catch(err){
            console.log(err);
        }
        alert(room)
    }
    render(){
        return(
            <div>
                <h1>Room Clean Requests</h1>
                <div>
                    {item.map(i=>
                    <div>
                         <p><strong>Room:</strong> {i.room}, <strong>Message:</strong> {i.clean}</p> 
                         <button onClick={()=>this.handleClick(i.room)}>Room Cleaned</button>
                    </div>   
                    )}
                </div>
            </div>
        )
    }

}
